import React, { useState } from 'react'
import blogService from '../services/blogs'

const Blog = ({ blog, user }) => {
  const [visible, setVisible] = useState(true)
  const [like, setLikes] = useState(blog.likes)
  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }
  const hideWhenVisible = { display: visible ? 'none': '' }
  const showWhenVisible = { display: visible ? '' : 'none'}

  const toggleVisibility = () => {
    setVisible(!visible)
  }

  const handleLikes = (event) => {
    event.preventDefault()
    const likes = like +1
    const id = blog.user.id
    console.log(likes)
    console.log(id)
    const newBlog = 
    {
      user: id,
      likes: likes,
      author: blog.author,
      title: blog.title,
      url: blog.url
    }
    blogService
    .update(blog.id, newBlog)
    .then(returned => {
      setLikes(returned.likes)
    })
  }

  const handleRemove = (event) => {
    event.preventDefault()
    const confirmed = window.confirm(`Are you sure, you want to delete ${blog.title} by ${blog.author}`)
    if (confirmed) {
      console.log('true')
      blogService.deleteBlog(blog.id)

    }
  }
  const usersBlog = blog.user.id === user.id
  return (
    <div style={blogStyle}>
      <div onClick={toggleVisibility} style={showWhenVisible}>
        {blog.title} {blog.author}
      </div>
      <div style ={hideWhenVisible}>
        <div onClick={toggleVisibility}>
          {blog.title} {blog.author}
        </div> 
          <br />
          <a href={blog.url}>{blog.url}</a>
          <br />
          likes: {like}
          <button onClick={handleLikes}>like</button>
          <p>Added by {blog.user.name}</p>
          {usersBlog ?
          <button onClick={handleRemove}>remove</button> :
          null}
      </div>
    </div>
  )
} 

export default Blog
