import React, { useState } from 'react'

const Blog = ({ blog }) => {
  const [visible, setVisible] = useState(true)

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }

  const show = () => (
      <div style={blogStyle}>

        {blog.title} {blog.author}
        <br />
        <a href={blog.url}>{blog.url}</a>
        <br />
        likes: {blog.likes}
        <button >like</button>
        <p>Added by {blog.user.name}</p>
      </div>
    )


  return (
    <div style={blogStyle}>
      <div onClick={() =>setVisible(!visible)}>
        {blog.title} {blog.author}
      </div>

    </div>
  )
} 

export default Blog
